mapper.select:
		select
		std_id,
		name,
		adr_lms,
		cls_lms,
		rate,
		photo,
		goal
		from
		sms_std
		
			rate >= #{rateValue}
			
				and
				
					LEFT(cls_lms, 2) = #{clsNo}
				
				
					LEFT(cls_lms, 4) = #{clsNo}
				
				
					cls_lms = #{clsNo}
				
			

			
				and
				
					LEFT(adr_lms,2) = #{addressNo}
				
				
					LEFT(adr_lms,4) = #{addressNo}
				
				
					adr_lms = #{addressNo}
				
			

			
				
				and (
				name like #{pattern1}
				or goal like #{pattern1}
				or cont like
				#{pattern1}
				
					or
					
						cls_lms = #{k}
					
				
				)
			
		
		order by
		std_id desc
		
			limit #{rowNo}, #{size}

		select
		std_id,
		name,
		adr_lms,
		cls_lms,
		rate,
		photo,
		goal
		from
		sms_std
		
			rate >= #{rateValue}
			
				and (
				
					IF ( LENGTH(#{c}) = 2, LEFT(cls_lms,2) = #{c}, LEFT(cls_lms,4) =
					#{c})
				
				)
			

			
				and
				
					LEFT(adr_lms,2) = #{addressNo}
				
				
					LEFT(adr_lms,4) = #{addressNo}
				
				
					adr_lms = #{addressNo}
				
			

			
				
				and (
				name like #{pattern1}
				or goal like #{pattern1}
				or cont like #{pattern1}
				
					or
					
						cls_lms = #{k}
					
				
				)
			

		
		order by
		std_id desc
		
			limit #{rowNo}, #{size}

		select count(*) from sms_std
		
