mapper.select:
select
  s.std_id,
  s.name,
  s.adr_lms,
  s.cls_lms,
  s.rate,
  s.photo,
  s.goal,
  c.name as cls_name
from
  sms_std s
  inner join sms_std_cls_l c 

  on s.cls_lms like #{pattern1}
where
  c.cls_l_id like #{pattern1}
  and s.rate >= #{rateValue}
  
  and LEFT(s.adr_lms,2) = #{addressNo}
  
   
  and LEFT(s.adr_lms,4) = #{addressNo}
  
   
  and s.adr_lms = #{addressNo}
  
order by
  std_id desc

  limit #{rowNo}, #{size}

select s.std_id, s.name, s.adr_lms, s.cls_lms, s.rate, s.photo, s.goal, c.name as cls_name from sms_std s inner join sms_std_cls_m c
on s.cls_lms like #{pattern1} where c.cls_m_id = RIGHT(#{clsNo},2) and c.cls_l_id = LEFT(#{clsNo},2) and s.rate >= #{rateValue}
and LEFT(s.adr_lms,2) = #{addressNo}
and LEFT(s.adr_lms,4) = #{addressNo}
and s.adr_lms = #{addressNo}
order by std_id desc
limit #{rowNo}, #{size}

select s.std_id, s.name, s.adr_lms, s.cls_lms, s.rate, s.photo, s.goal, c.name as cls_name from sms_std s inner join sms_std_cls_s c on s.cls_lms = #{clsNo} where c.cls_s_id = RIGHT(#{clsNo},2) and c.cls_m_id = MID(#{clsNo},3,2) and c.cls_l_id = LEFT(#{clsNo},2) and s.rate >= #{rateValue}
and LEFT(s.adr_lms,2) = #{addressNo}
and LEFT(s.adr_lms,4) = #{addressNo}
and s.adr_lms = #{addressNo}
order by std_id desc
limit #{rowNo}, #{size}

select count(*) from sms_std
where

LEFT(cls_lms,2) = #{clsNo}

  
    and LEFT(adr_lms,2) = #{addressNo}
    
     
    and LEFT(adr_lms,4) = #{addressNo}
    
     
    and adr_lms = #{addressNo}
    
  

 
LEFT(cls_lms,4) = #{clsNo}

 
  and LEFT(adr_lms,2) = #{addressNo}
  
   
  and LEFT(adr_lms,4) = #{addressNo}
  
   
  and adr_lms = #{addressNo}
  


 
LEFT(cls_lms,6) = #{clsNo}

 
  and LEFT(adr_lms,2) = #{addressNo}
  
   
  and LEFT(adr_lms,4) = #{addressNo}
  
   
  and adr_lms = #{addressNo}
